mapper.select:
SELECT COUNT(*) TOTAL_ROW FROM EAC_APPROVAL A, EAC_APPROVAL_ITEM AD WHERE 1=1 AND A.COMPANY_CD = #{companyCode} AND A.DEL_YN = 'N' AND A.COMPANY_CD = AD.COMPANY_CD AND A.APPROVAL_ID = AD.APPROVAL_ID AND A.APPROVAL_STATUS_CD = 'SR'
AND A.BUDGET_DEPT_CD = #{budgetDeptCd}
AND AD.PRODUCT_CD = #{productCd}
AND AD.AUTH_DATE LIKE #{authDate}||'%' -- 검색조건 : 승인년/월

-- ========================================== -- 심포지움관리자가 품의작성할 결재요청건 조회 -- ========================================== SELECT A.COMPANY_CD , A.APPROVAL_ID AS S_APPROVAL_ID -- 심포지움 품의서ID , AD.SEQ AS S_APPROVAL_SEQ -- 심포지움 품의서 내역 일련번호 , AD.AUTH_DATE , AD.MERC_NAME , AD.AMT_AMOUNT , AD.VAT_AMOUNT , AD.REQUEST_AMOUNT , AD.USER_ID , AD.USER_NM , AD.USER_NAME , A.BUDGET_DEPT_CD , A.BUDGET_DEPT_NM , A.ACCOUNT_CD , A.ACCOUNT_NM , AD.ACCOUNT2_CD , AD.ACCOUNT2_NM , AD.PRODUCT_CD , AD.PRODUCT_NM , AD.CUSTOMER_CD , AD.CUSTOMER_NM , AD.FTR_CD , AD.DETAILS , A.RQ_OPINION FROM EAC_APPROVAL A, EAC_APPROVAL_ITEM AD WHERE 1=1 AND A.COMPANY_CD = #{companyCode} AND A.DEL_YN = 'N' AND A.COMPANY_CD = AD.COMPANY_CD AND A.APPROVAL_ID = AD.APPROVAL_ID AND A.APPROVAL_STATUS_CD = 'SR'
AND A.BUDGET_DEPT_CD = #{budgetDeptCd}
AND AD.PRODUCT_CD = #{productCd}
AND AD.AUTH_DATE LIKE #{authDate}||'%' -- 검색조건 : 승인년/월
ORDER BY AUTH_DATE DESC

