-- ============================================================ -- 심포지움 상신 요청건수에 대한 첨부테이블 복사 -- ============================================================
INSERT INTO EAC_APPROVAL_ATTACH ( COMPANY_CD, APPROVAL_ID, SEQ, ATTACH_ID, APPROVAL_ATTACH_TP_CD, REG_DTTM, REG_USER_ID ) SELECT COMPANY_CD, #{approvalId},
(SELECT SEQ FROM EAC_APPROVAL_ITEM WHERE S_APPROVAL_ID = #{sApprovalId} and APPROVAL_ID = #{approvalId}) as SEQ, ATTACH_ID, APPROVAL_ATTACH_TP_CD, TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS'), #{regUserId} FROM EAC_APPROVAL_ATTACH WHERE 1 = 1 AND COMPANY_CD = #{companyCode} AND APPROVAL_ID = #{sApprovalId}

mapper.update:
UPDATE SYS_ATTACH_FILE 
SET 
	PFILE_NM = #{pFileName}
WHERE 1=1
	AND ATTACH_ID = #{attachId}
	AND SEQ = #{attachSeq}

mapper.select:
SELECT
	/** [ApprovalRqMapper.validRqUser] [전결 - 본인이 상신자인지 체크] */
		COUNT(1)
			FROM
				EAC_APPROVAL
					WHERE 1 = 1
						AND COMPANY_CD = #{companyCode}
						AND APPROVAL_ID = #{approvalId}
						AND RQ_USER_ID = #{updtUserId}

SELECT
	/** [ApprovalRqMapper.validLastApprovalUser] [전결 - 본인이 최종 결재 예정자인지 체크] */
		COUNT(1)
			FROM
				EAC_APPROVAL_LINE
					WHERE 1 = 1
						AND COMPANY_CD = #{companyCode}
						AND APPROVAL_ID = #{approvalId}
						AND SEQ =
						(
							SELECT SEQ FROM
								(
								SELECT * FROM EAC_APPROVAL_LINE
									WHERE 1 = 1
										AND COMPANY_CD = #{companyCode}
										AND APPROVAL_ID = #{approvalId}
											ORDER BY SEQ DESC
								)
									WHERE 1 = 1
										AND ROWNUM <= 1
						)
						AND AP_USER_ID = #{updtUserId}

SELECT
	/** [ApprovalRqMapper.validCancelSymposium] [심포지움 데이터를 삭제할 수 있는지 체크] */
		COUNT(1)
			FROM
				EAC_APPROVAL_ITEM
					WHERE 1 = 1
						AND COMPANY_CD = #{companyCd}
						AND S_APPROVAL_ID = #{approvalId}

