						AND ATTACH_ID = #{attachId}

DELETE
		/** [ApprovalRqMapper.deleteSymposiumApprovalAttach] [품의서 결재라인 삭제] */
  FROM EAC_APPROVAL_LINE
 WHERE COMPANY_CD   = #{companyCd}
